import { gql } from '@apollo/client'


export const ALL_AUTHORS = gql`
  query {
    allAuthors {
      name
      born
      bookCount
    }
  }
`
export const ALL_BOOKS = gql`
  query {
    allBooks {
      title
      author {
        name
      }
      published
      genres
    }
  }
`
export const ALL_GENRES = gql`
  query {
    allBooks {
      genres
    }
  }
`
export const BOOKS_BY_GENRE = gql`
  query findBooks($genre: String!) {
    allBooks(
      genre: $genre
    ) {
      title
      author {
        name
      }
    }
  }
`

export const CURRENT_USER = gql`
  query {
    me {
      username
      favoriteGenre
    }
  }
`
