import React, { useState, useEffect } from 'react'
import { useHistory } from 'react-router-dom'

import { useQuery, useMutation, useLazyQuery } from '@apollo/client'
import { CURRENT_USER, BOOKS_BY_GENRE } from '../gql/queries'
import { LOGIN } from '../gql/mutations'

const Recommendations = (props) => {
    const [queryUser, result] = useLazyQuery(CURRENT_USER, {
        fetchPolicy: "no-cache",
        onError: (e) => {
            console.log(e.message)
        }
    })

    const [queryBooks, bookResult] = useLazyQuery(BOOKS_BY_GENRE, {
        fetchPolicy: "no-cache",
        onError: (e) => {
            console.log(e.message)
        }
    })

    const [favoriteGenre, setFavoriteGenre] = useState(null)
    const [recommendedBooks, setRecommendedBooks] = useState([])

    useEffect(() => {
        if (result.data && result.data.me) {
            setFavoriteGenre(result.data.me.favoriteGenre)
        } else {
            queryUser()
        }
    }, [result, favoriteGenre])

    useEffect(() => {
        queryBooks({ variables: { genre: favoriteGenre }})
        console.log(bookResult)
        setRecommendedBooks(bookResult.data)
    }, [result, favoriteGenre])


    if (!props.show) {
        return null
    }

    if (!favoriteGenre) {
        return null
    }

    console.log(bookResult)
    //const recommendedBooks = props.books.allBooks.filter(b => b.genres.includes(favoriteGenre))

    return(
        <div>
            <h2>Recommendations</h2>
            <p>books in your favorite genre <b>{favoriteGenre}</b></p>
            <table>
                <tbody>
                    <tr>
                        <th>
                        </th>
                        <th>author</th>
                        <th>published</th>
                    </tr>
                    {recommendedBooks.map(b => 
                        <tr key = {b.title}>
                            <td>{b.title}</td>
                            <td>{b.author.name}</td>
                            <td>{b.published}</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    )
}

export default Recommendations
