import React, { useEffect, useState } from 'react'

import Authors from './components/Authors'
import Books from './components/Books'
import NewBook from './components/NewBook'
import LoginForm from './components/LoginForm'
import Recommendations from './components/Recommendations'

import { useApolloClient, useQuery, useLazyQuery } from '@apollo/client'
import { ALL_AUTHORS, ALL_BOOKS, ALL_GENRES, CURRENT_USER } from './gql/queries'

import SetBirthyear from './components/SetBirthyear'

//TARKISTUS
//8.20

const App = () => {
  const [page, setPage] = useState('authors')
  const [token, setToken] = useState(null)
  const client = useApolloClient()

  useEffect(() => {
    setToken(localStorage.getItem('user-token'))
  }, [])

  const authors = useQuery(ALL_AUTHORS)
  const books = useQuery(ALL_BOOKS)
  const genres = useQuery(ALL_GENRES)

  if (books.loading || authors.loading) {
    return <div>loading...</div>
  }

  const logout = () => {
    setToken(null)
    localStorage.clear()
    client.resetStore()
  }

  const clickRecommend = () => {
    const user = useLazyQuery(CURRENT_USER)
  }

  if (!token) {
    return (
      <div>
        <h2>Login</h2>
        <LoginForm
          setToken = {setToken}
        />
      </div>
    )
  }

  return (
    <div>
      <div>
        <button onClick={() => setPage('authors')}>authors</button>
        <button onClick={() => setPage('books')}>books</button>
        <button onClick={() => setPage('add')}>add book</button>
        <button onClick={() => setPage('update')}>update author</button>
        <button onClick= {clickRecommend}>recommend</button>
        <button onClick = {logout}>logout</button>
      </div>
        <Authors
          show = {page === 'authors'} authors = {authors.data}
        />

        <Books
          show = {page === 'books'} books = {books.data} genres = {genres.data}
        />

        <NewBook
          show = {page === 'add'}
        />

        <SetBirthyear
          show = {page === 'update'} authors = {authors.data}
        />

        <Recommendations
          show = {page === 'recommend'} books = {books.data}
        />
    </div>
  )
}

export default App
